        dbManager_description.insertToDB("Пульсоксиметр", "Медицинский контрольно-диагностический прибор для неинвазивного измерения уровня насыщения кислородом капиллярной крови");
        dbManager_description.insertToDB("Стетоскоп", " Прибор служит для прослушивания тонов Короткова при измерении давления механическим тонометром, а также для прослушивания легких.");
        dbManager_description.insertToDB("Бинт", "Стерильный марлевый бинт применяется для фиксации повязок. Бинт может быть частью как домашней аптечки, так и набора скорой помощи. Бинты подпрессованы с краев, что предотвращает их размотку при выпадении из руки в момент наложения повязки.");
